INSERT INTO `codo_index_step` (`create_time`, `update_time`, `id`, `title`, `description`, `cards`, `faqs`) VALUES ('2024-12-02 17:51:38', '2024-12-02 17:51:38', 6, '业务权限配置', '一站式配置业务、权限与角色', '\"{\\n  \\\"all\\\": [\\n    {\\n      \\\"title\\\": \\\"业务配置\\\",\\n      \\\"description\\\": \\\"管理后台\\\",\\n      \\\"icon\\\": \\\"mg\\\",\\n      \\\"href\\\": \\\"/mg/authority/business\\\",\\n      \\\"stepDesc\\\":\\\"允许根据业务需求调整业务配置。可通过直观页面，轻松配置权限、工作流、数据映射和界面元素等，有效提高系统的灵活性和可维护性。\\\"\\n    },\\n    {\\n      \\\"title\\\": \\\"用户配置\\\",\\n      \\\"description\\\": \\\"管理后台\\\",\\n      \\\"icon\\\": \\\"mg\\\",\\n      \\\"href\\\": \\\"/mg/authority/user-list\\\",\\n      \\\"stepDesc\\\":\\\"用户信息的创建、编辑和权限分配。支持用户角色管理，实现不同用户组的角色分级，确保数据安全和业务流程的合规性，并增强系统的整体安全性。\\\"\\n    },\\n    {\\n      \\\"title\\\": \\\"权限配置\\\",\\n      \\\"description\\\": \\\"管理后台\\\",\\n      \\\"icon\\\": \\\"mg\\\",\\n      \\\"href\\\": \\\"/mg/authority/functions\\\",\\n      \\\"stepDesc\\\":\\\"根据用户角色和职责分配访问权限，确保数据安全和操作合规。支持细粒度的权限管理，实现权限的灵活分配和有效监控，提升系统的安全性和易用性。\\\"      \\n    }\\n  ]\\n}\"', '\"{\\n \\\"all\\\": [\\n    {\\n        \\\"question\\\": \\\"如何创建一个新业务？\\\",\\n        \\\"answer\\\": [\\\"首先进入管理后台，在\\\\\\\"业务管理\\\\\\\"模块创建业务，然后在\\\\\\\"权限管理\\\\\\\"中配置相应的角色和权限。\\\",\\\"新建业务时，请务必关联用户组，完成业务授权。\\\"]\\n    },\\n    {\\n        \\\"question\\\": \\\"为什么新来的同事可能找不到该用户？\\\",\\n        \\\"answer\\\": [\\\"管理后台会定时同步用户数据，来自用户中心或飞书等平台。\\\",\\\"如果是外包用户，则需要由管理员手动添加。\\\"]\\n    },\\n    {\\n        \\\"question\\\": \\\"飞书或钉钉登录问题怎么办？\\\",\\n        \\\"answer\\\": [\\\"系统会将用户属性与飞书、钉钉的关联ID绑定。\\\",\\\"如果登录不成功，请联系管理员，核实关联ID的正确性。\\\"]\\n    },\\n    {\\n        \\\"question\\\": \\\"关于角色的问题\\\",\\n        \\\"answer\\\": [\\\"通用角色：可理解为用户组，主要属性是用户集合。通常在授权时，将用户添加到用户组即可。\\\",\\\"基础角色：系统默认的角色集合，包含权限基础配置。在用户组授权时，需要关联基础角色来完成细化权限分配。\\\"]\\n    },\\n    {\\n        \\\"question\\\": \\\"为什么我的菜单和其他同事的不一样？\\\",\\n        \\\"answer\\\": [\\\"系统菜单是基于权限控制的，显示内容可能因权限不同而有所差异。\\\",\\\"如需访问其他菜单，请联系管理员为您的账号添加相应权限。\\\"]\\n    },\\n    {\\n        \\\"question\\\": \\\"403报错怎么解决？\\\",\\n        \\\"answer\\\": [\\\"403报错通常是由于权限不足引起的。\\\",\\\"可能是新功能尚未配置权限，联系管理员进行权限调整。\\\"]\\n    }   \\n  ]\\n}\"');
INSERT INTO `codo_index_step` (`create_time`, `update_time`, `id`, `title`, `description`, `cards`, `faqs`) VALUES ('2024-12-02 18:43:15', '2024-12-02 18:43:15', 7, '资源配置', '多云管理、Agent部署与资源模型配置', '\"{\\n    \\\"all\\\": [\\n        {\\n            \\\"title\\\": \\\"服务树配置\\\",\\n            \\\"description\\\": \\\"配置管理【CMDB】\\\",\\n            \\\"icon\\\": \\\"cmdb\\\",\\n            \\\"href\\\": \\\"/cmdb/service-tree\\\",\\n            \\\"stepDesc\\\": \\\"允许根据业务逻辑和组织结构创建服务树模型。支持自定义服务节点，实现服务与资产的关联映射，便于管理和追踪服务依赖和影响范围。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"动态分组\\\",\\n            \\\"description\\\": \\\"配置管理【CMDB】\\\",\\n            \\\"icon\\\": \\\"cmdb\\\",\\n            \\\"href\\\": \\\"/cmdb/bizmg/cmdb-dynamic-group\\\",\\n            \\\"stepDesc\\\": \\\"允许根据不同的查询条件创建资源聚合组，这些分组能够动态地响应资源的增加或减少。可以基于集群、模块或主机的属性来定义查询条件，从而实现灵活配置。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"堡垒机权限分组\\\",\\n            \\\"description\\\": \\\"配置管理【CMDB】\\\",\\n            \\\"icon\\\": \\\"cmdb\\\",\\n            \\\"href\\\": \\\"/cmdb/bizmg/cmdb-perm-group\\\",\\n            \\\"stepDesc\\\": \\\"通过角色定义权限并进一步关联用户的方式间接赋予堡垒机权限。支持细颗粒度的权限访问控制。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"构建机管理\\\",\\n            \\\"description\\\": \\\"任务管理【Flow】\\\",\\n            \\\"icon\\\": \\\"flow\\\",\\n            \\\"href\\\": \\\"/flow#/job-meta/agent-list\\\",\\n            \\\"stepDesc\\\": \\\"构建机管理是自动化构建过程中的关键环节，负责编译代码、运行测试并生成可部署的软件包。通过持续集成工具监控源代码变更，自动触发构建流程。\\\"\\n        }\\n    ]\\n}\"', '\"{\\n    \\\"all\\\": [\\n        {\\n            \\\"question\\\": \\\"资源是如何录入的？\\\",\\n            \\\"answer\\\": \\\"资源主要通过以下方式录入： \\\\n 1. 定时从云厂商的 API 自动同步获取。\\\\n  2. 支持手动添加一些特殊资源。 \\\\n 3. （未来功能）通过 Agent 自动同步录入资源。\\\"\\n        },\\n        {\\n            \\\"question\\\": \\\"资源如何使用和管理？\\\",\\n            \\\"answer\\\": [\\n                \\\"<b>构建机组:</b> 推荐使用 <b>构建机组</b> 进行管理，以统一控制构建资源的使用。\\\",\\n                \\\"<b>服务树:</b> 适用于资源的层次化管理，是运维规范中要求线上资产必须录入的基础工具。\\\",\\n                \\\"<b>动态分组（推荐）:</b> 根据资源属性或服务树规则动态调整分组，灵活高效，建议优先选择。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"云区域的作用和管理\\\",\\n            \\\"answer\\\": [\\n                \\\"<b>堡垒机网域功能</b>\\\\n云区域用于管理特定区域内堡垒机的下网域数据，确保资源分区清晰可控。\\\",\\n                \\\"<b>Agent 校准与管理:</b> \\\\n 1. 系统会根据预设规则，自动调整 CMDB 中录入的 Agent ID。\\\\n  2.调整后，将部署的 Agent ID 与 CMDB 中的资产 ID 进行关联。\\\\n 3.关联完成后，即可实现云区域内资源的统一管理。\\\"\\n            ]\\n        },\\n    {\\n            \\\"question\\\": \\\"堡垒机权限分组如何设置？\\\",\\n            \\\"answer\\\": [\\n                \\\"系统支持将服务树中的资源与第三方堡垒机进行<b>资产和权限同步</b>。\\\",\\n                \\\"同步完成后，可以直接为系统用户分配堡垒机权限，快速实现访问控制。\\\"\\n            ]\\n        },\\n    {\\n            \\\"question\\\": \\\"Agent的部署和作用\\\",\\n            \\\"answer\\\": [\\n                \\\"Agent解释: Agent 是系统最底层的管理管道，所有资产管理（包括打包构建、线上主机、K8S等）均依赖 Agent。\\\",\\n                \\\"<b>常见问题:</b> 如果发现 Agent 识别错误或无法上报资源，请立即联系管理员。\\\",\\n                \\\"<b>安全注意事项:</b> 安全 007 系统依赖 Agent 运行，如资源未正确安装 Agent，可能导致资源被安全 007 系统强制格式化。\\\"\\n            ]\\n        }\\n    ]\\n}\"');
INSERT INTO `codo_index_step` (`create_time`, `update_time`, `id`, `title`, `description`, `cards`, `faqs`) VALUES ('2024-12-02 18:45:22', '2024-12-02 18:45:22', 8, '自动化工作流', '持续构建与集成、发布管理及工单流程', '\"{\\n    \\\"打包构建\\\": [\\n        {\\n            \\\"title\\\": \\\"CI/CD持续集成\\\",\\n            \\\"description\\\": \\\"研发效能【行云】\\\",\\n            \\\"icon\\\": \\\"xingyun\\\",\\n            \\\"width\\\": \\\"55px\\\",\\n            \\\"smallWidth\\\": \\\"45px\\\",\\n            \\\"href\\\": \\\"/flow#/jobCenter/common-flow\\\",\\n            \\\"stepDesc\\\": \\\"通过CI/CD持续集成，实现开发生命周期中的自动化测试、频繁代码合并、快速交付和自动化部署，有效提高软件开发的效率和质量。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"构建机管理\\\",\\n            \\\"description\\\": \\\"任务管理【Flow】\\\",\\n            \\\"icon\\\": \\\"flow\\\",\\n            \\\"href\\\": \\\"/flow#/job-meta/agent-list\\\",\\n            \\\"stepDesc\\\": \\\"构建机管理是自动化构建过程中的关键环节，负责编译代码、运行测试并生成可部署的软件包。通过持续集成工具监控源代码变更，自动触发构建流程。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"凭证管理\\\",\\n            \\\"description\\\": \\\"任务管理【Flow】\\\",\\n            \\\"icon\\\": \\\"flow\\\",\\n            \\\"href\\\": \\\"/flow#/job-meta/voucher\\\",\\n            \\\"stepDesc\\\": \\\"保护敏感信息如密码、密钥和访问令牌。通过集中存储和访问控制，确保只有授权用户才能访问这些凭证。凭证管理系统简化了凭证的创建、分发、更新和撤销流程。\\\"\\n        }\\n    ],\\n    \\\"发布管理\\\": [\\n        {\\n            \\\"title\\\": \\\"CI/CD发布管理\\\",\\n            \\\"description\\\": \\\"研发效能【行云】\\\",\\n            \\\"icon\\\": \\\"xingyun\\\",\\n            \\\"width\\\": \\\"55px\\\",\\n            \\\"smallWidth\\\": \\\"45px\\\",\\n            \\\"href\\\": \\\"/flow#/jobCenter/common-flow\\\",\\n            \\\"stepDesc\\\": \\\"通过CI/CD发布管理自动化构建、测试和部署流程，加快软件交付，提升质量和效率，确保快速反馈和持续改进。\\\"\\n        }\\n    ],\\n    \\\"流程配置\\\": [\\n        {\\n            \\\"title\\\": \\\"配置自动化流程\\\",\\n            \\\"description\\\": \\\"任务管理【Flow】\\\",\\n            \\\"icon\\\": \\\"flow\\\",\\n            \\\"href\\\": \\\"/flow#/workFlow/flowDesign\\\",\\n            \\\"stepDesc\\\": \\\"在软件开发和部署过程中通过预设的自动化规则和工具来管理任务和步骤的配置。简化复杂的操作流程，提高执行效率和准确性，同时减少人为错误。\\\"\\n        }\\n    ]\\n}\"', '\"{\\n    \\\"all\\\": [\\n        {\\n            \\\"question\\\": \\\"前置任务\\\",\\n            \\\"answer\\\": [\\n                \\\"<b>创建业务与申请权限:</b> 需要确保已创建业务并申请相关权限（查看指南）。\\\",\\n                \\\"<b>添加仓库:</b> 如果是构建类项目，请将对应代码仓库关联至系统（查看操作）。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"运行资源设置\\\",\\n            \\\"answer\\\": [\\n                \\\"<b>部署 Agent:</b> 流水线运行依赖机器或 K8S 集群中的 Agent，请参考 Agent 部署文档 进行安装与配置。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"流程篇\\\",\\n            \\\"answer\\\": [\\n                \\\"每个流水线需要关联一个 <b>CODO 流程</b>（支持多个流水线关联同一流程）。\\\",\\n                \\\"在执行时，CODO 流程会将任务拆分为多个 <b>订单（执行记录）</b>，由 Agent 依次执行。\\\",\\n                \\\"<b>流程编排:</b> 通过流程编排工具设计流水线的任务逻辑，满足具体需求。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"元数据配置\\\",\\n            \\\"answer\\\": [\\n                \\\"配置执行所需的关键数据，包括:\\\\n<b>执行脚本</b>\\\\n<b>无服务声明文件</b>\\\\n<b>凭证信息</b>这些元数据将为流程编辑提供必要支持。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"表单设置\\\",\\n            \\\"answer\\\": [\\n                \\\"创建表单:\\\\n在<b>行云</b>平台新建表单，选择<b>持续集成</b> 模板，进行以下设置：\\\\n<b>流水线-基础设置：</b> 定义流水线的基本信息。\\\\n<b>流水线-自定义参数：</b>配置所需的输入参数。\\\\n<b>流水线-触发设置：</b>选择触发方式（仓库触发、Webhook、定时任务或表单触发）。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"执行与调试\\\",\\n            \\\"answer\\\": [\\n                \\\"填写表单并执行流水线任务。\\\"\\n            ]\\n        }\\n        \\n    \\n    ]\\n}\"');
INSERT INTO `codo_index_step` (`create_time`, `update_time`, `id`, `title`, `description`, `cards`, `faqs`) VALUES ('2024-12-02 18:46:12', '2024-12-02 18:46:12', 9, '服务器环境管理', '管理服务器、容器集群、数据库等', '\"{\\n    \\\"all\\\": [\\n        {\\n            \\\"title\\\": \\\"堡垒机管理\\\",\\n            \\\"description\\\": \\\"堡垒机\\\",\\n            \\\"icon\\\": \\\"jumpserver\\\",\\n            \\\"href\\\": \\\"#\\\",\\n            \\\"width\\\": \\\"45px\\\",\\n            \\\"smallWidth\\\": \\\"35px\\\",\\n            \\\"stepDesc\\\": \\\"集中管理用户访问权限，监控运维操作，增强系统安全性。通过权限控制、审计追踪和风险告警，确保最小权限原则，预防内部安全威胁。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"云原生管理\\\",\\n            \\\"description\\\": \\\"云原生管理【CNMP】\\\",\\n            \\\"icon\\\": \\\"cnmp\\\",\\n            \\\"href\\\": \\\"/cnmp/dashboard\\\",\\n            \\\"stepDesc\\\": \\\"提供多云与多集群管理、DevOps支持、应用生命周期管理、微服务治理等功能，简化DevOps工作流，以实现应用程序的快速交付和高效运行。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"数据库审计查询\\\",\\n            \\\"description\\\": \\\"数据库审计平台\\\",\\n            \\\"icon\\\": \\\"database\\\",\\n            \\\"stepDesc\\\": \\\"通过对数据库操作进行审计，实现对数据库操作的全面监控，保障数据安全。\\\"\\n        }\\n    ]\\n}\"', '\"{\\n    \\\"all\\\": [\\n        {\\n            \\\"question\\\": \\\"如何管理堡垒机？\\\",\\n            \\\"answer\\\": [\\n                \\\"<b>参考资源配置文档：</b> 使用自动化权限管理工具来管理堡垒机的访问权限。详细的操作步骤请参考 堡垒机自动化权限管理文档。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"如何添加云原生集群？\\\",\\n            \\\"answer\\\": [\\n                \\\"创建 K8S 集群。\\\",\\n                \\\"在 <b>云原生管理界面</b> 中，将集群信息录入并保存，完成后，即可继续进行对集群的管理和操作。\\\"\\n            ]\\n        },\\n        {\\n            \\\"question\\\": \\\"云原生集群的用户授权\\\",\\n            \\\"answer\\\": [\\n                \\\"<b>通用权限：</b> 确保当前用户的 <b>用户组</b> 在权限中心已经完成菜单和接口权限配置。\\\",\\n                \\\"<b>资源权限：</b> 进入<b>云原生集群</b> 的 <b>用户授权页面</b>，将 <b>权限中心的用户组</b> 与 <b>云原生集群</b>权限进行关联，从而完成集群的访问控制。\\\"\\n            ]\\n        }\\n    \\n    ]\\n}\"');
INSERT INTO `codo_index_step` (`create_time`, `update_time`, `id`, `title`, `description`, `cards`, `faqs`) VALUES ('2024-12-02 18:47:00', '2024-12-02 18:47:00', 10, '可观测', '监控告警、链路追踪及日志查看与分析', '\"{\\n    \\\"all\\\": [\\n        {\\n            \\\"title\\\": \\\"日志查询\\\",\\n            \\\"description\\\": \\\"统一日志查询平台【CLS】\\\",\\n            \\\"icon\\\": \\\"log\\\",\\n            \\\"href\\\": \\\"#\\\",\\n            \\\"stepDesc\\\": \\\"帮助用户快速定位系统事件、错误信息和性能数据。通过日志查询工具高效地从海量日志数据中提取有用信息，支持文本搜索、正则表达式匹配和复杂查询语句。提高问题诊断的速度和准确性，同时助力安全审计和合规性检查。\\\"\\n        },\\n        {\\n            \\\"title\\\": \\\"监控查询\\\",\\n            \\\"description\\\": \\\"监控查询平台【Grafana】\\\",\\n            \\\"icon\\\": \\\"grafana\\\",\\n            \\\"href\\\": \\\"#\\\",\\n            \\\"stepDesc\\\": \\\"支持多种数据源，如Prometheus、InfluxDB、MySQL、Elasticsearch等，提供包括数据可视化、监控和报警、度量和报告等多样化可观测数据，帮助用户实时监控系统性能指标。\\\"\\n        }\\n    ]\\n}\"', '\"{\\n \\\"all\\\": [\\n    {\\n        \\\"question\\\": \\\"如何使用日志查询平台？\\\",\\n        \\\"answer\\\": \\\"在日志查询界面，可以输入查询条件，查看相应的日志信息。\\\"\\n    },\\n    {\\n        \\\"question\\\": \\\"如何使用监控查询平台？\\\",\\n        \\\"answer\\\": \\\"在监控查询界面，可以输入查询条件，查看相应的监控信息。\\\"\\n    }\\n  ]\\n}\"');
